import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import axios from 'axios';

export default function Attendance() {
  const { t } = useTranslation();
  const [attendance, setAttendance] = useState([]);
  const [form, setForm] = useState({ studentId: '', date: '', status: 'present' });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchAttendance();
  }, []);

  const fetchAttendance = async () => {
    try {
      const response = await axios.get('/api/attendance');
      setAttendance(response.data);
    } catch (error) {
      console.error('Error fetching attendance:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post('/api/attendance', form);
      setMessage('Attendance marked successfully!');
      setForm({ studentId: '', date: '', status: 'present' });
      fetchAttendance();
    } catch (error) {
      setMessage('Error marking attendance.');
    }
    setLoading(false);
  };

  return (
    <div className="attendance-container">
      <div className="card">
        <h2>üìù {t('attendance.title')}</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder={t('attendance.studentId')}
            value={form.studentId}
            onChange={(e) => setForm({ ...form, studentId: e.target.value })}
            required
          />
          <input
            type="date"
            value={form.date}
            onChange={(e) => setForm({ ...form, date: e.target.value })}
            required
          />
          <select
            value={form.status}
            onChange={(e) => setForm({ ...form, status: e.target.value })}
          >
            <option value="present">{t('attendance.present')}</option>
            <option value="absent">{t('attendance.absent')}</option>
          </select>
          <button type="submit" disabled={loading}>
            {loading ? '‚è≥' : '‚úÖ'} {t('attendance.mark')}
          </button>
        </form>
        {message && <p className={message.includes('Error') ? 'error' : 'success'}>{message}</p>}
      </div>
      <div className="card">
        <h3>üìã {t('attendance.records')}</h3>
        <ul className="attendance-list">
          {attendance.map((record) => (
            <li key={record.id}>
              <strong>{record.student_id}</strong> - {record.date} - {record.status}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
