import React, { useRef, useState } from 'react';
import { useTranslation } from 'react-i18next';

export default function FaceRecognition() {
  const { t } = useTranslation();
  const videoRef = useRef(null);
  const [isStreaming, setIsStreaming] = useState(false);
  const [message, setMessage] = useState('');

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      setIsStreaming(true);
      setMessage('Camera started. Face recognition active.');
      // TODO: Integrate MediaPipe for face detection
    } catch (error) {
      setMessage('Error accessing camera.');
      console.error(error);
    }
  };

  const stopCamera = () => {
    if (videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
      setIsStreaming(false);
      setMessage('Camera stopped.');
    }
  };

  return (
    <div className="face-recognition-container">
      <div className="card">
        <h2>üì∑ {t('faceRecognition.title')}</h2>
        <div className="video-container">
          <video ref={videoRef} autoPlay playsInline muted />
          {isStreaming && <div className="overlay">üë§ Face Detected</div>}
        </div>
        <div className="controls">
          {!isStreaming ? (
            <button onClick={startCamera}>‚ñ∂Ô∏è {t('faceRecognition.start')}</button>
          ) : (
            <button onClick={stopCamera}>‚èπÔ∏è {t('faceRecognition.stop')}</button>
          )}
        </div>
        {message && <p className="message">{message}</p>}
      </div>
    </div>
  );
}
